mapper.select:
        SELECT


--         级联表的字段

        FROM caipin  a



                and a.id in

                #{item}



                and a.caipin_name like CONCAT('%',#{params.caipinName},'%')


                and a.caipin_uuid_number like CONCAT('%',#{params.caipinUuidNumber},'%')


                and a.caipin_types = #{params.caipinTypes}


                  and a.caipin_kucun_number >= #{params.caipinKucunNumberStart}   


                  and a.caipin_kucun_number <= #{params.caipinKucunNumberEnd}   


                and a.caipin_kucun_number = #{params.caipinKucunNumber}


                  and a.caipin_old_money >= #{params.caipinOldMoneyStart}   


                  and a.caipin_old_money <= #{params.caipinOldMoneyEnd}   


                  and a.caipin_new_money >= #{params.caipinNewMoneyStart}   


                  and a.caipin_new_money <= #{params.caipinNewMoneyEnd}   


                  and a.caipin_clicknum >= #{params.caipinClicknumStart}   


                  and a.caipin_clicknum <= #{params.caipinClicknumEnd}   


                and a.caipin_clicknum = #{params.caipinClicknum}


                and a.caipin_content like CONCAT('%',#{params.caipinContent},'%')


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.caipin_delete >= #{params.caipinDeleteStart}   


                  and a.caipin_delete <= #{params.caipinDeleteEnd}   


                and a.caipin_delete = #{params.caipinDelete}




        order by a.${params.orderBy} desc
