mapper.select:
        SELECT


--         级联表的字段
        ,caipin.caipin_name as caipinName
        ,caipin.caipin_uuid_number as caipinUuidNumber
        ,caipin.caipin_photo as caipinPhoto
        ,caipin.caipin_types as caipinTypes
        ,caipin.caipin_kucun_number as caipinKucunNumber
        ,caipin.caipin_old_money as caipinOldMoney
        ,caipin.caipin_new_money as caipinNewMoney
        ,caipin.caipin_clicknum as caipinClicknum
        ,caipin.caipin_content as caipinContent
        ,caipin.shangxia_types as shangxiaTypes
        ,caipin.caipin_delete as caipinDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney

        FROM cart  a
        left JOIN caipin caipin ON a.caipin_id = caipin.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.caipin_id = #{params.caipinId}
                )


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.caipin_id IS NOT NULL


                and caipin.caipin_name like CONCAT('%',#{params.caipinName},'%')


                and caipin.caipin_uuid_number like CONCAT('%',#{params.caipinUuidNumber},'%')


                and caipin.caipin_types = #{params.caipinTypes}



                  and caipin.caipin_kucun_number >= #{params.caipinKucunNumberStart}   


                  and caipin.caipin_kucun_number <= #{params.caipinKucunNumberEnd}   


                and caipin.caipin_kucun_number = #{params.caipinKucunNumber}


                  and caipin.caipin_old_money >= #{params.caipinOldMoneyStart}   


                  and caipin.caipin_old_money <= #{params.caipinOldMoneyEnd}   


                  and caipin.caipin_new_money >= #{params.caipinNewMoneyStart}   


                  and caipin.caipin_new_money <= #{params.caipinNewMoneyEnd}   


                  and caipin.caipin_clicknum >= #{params.caipinClicknumStart}   


                  and caipin.caipin_clicknum <= #{params.caipinClicknumEnd}   


                and caipin.caipin_clicknum = #{params.caipinClicknum}


                and caipin.caipin_content like CONCAT('%',#{params.caipinContent},'%')


                and caipin.shangxia_types = #{params.shangxiaTypes}



                  and caipin.caipin_delete >= #{params.caipinDeleteStart}   


                  and caipin.caipin_delete <= #{params.caipinDeleteEnd}   


                and caipin.caipin_delete = #{params.caipinDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   



        order by a.${params.orderBy} desc
